package fantasy0v0.dinoFx;

import fantasy0v0.dinoFx.resources.Resources;
import fantasy0v0.dinoFx.sprite.Game;
import fantasy0v0.dinoFx.utils.Time;
import javafx.animation.AnimationTimer;
import javafx.application.Application;
import javafx.scene.Scene;
import javafx.scene.canvas.Canvas;
import javafx.scene.canvas.GraphicsContext;
import javafx.scene.layout.Pane;
import javafx.stage.Stage;

public class App extends Application {

  public static void main(String[] args) {
    launch(args);
  }

  @Override
  public void start(Stage primaryStage) {
    Pane root = new Pane();
    Canvas canvas = new Canvas();
    canvas.heightProperty().bind(root.heightProperty());
    canvas.widthProperty().bind(root.widthProperty());

    Resources.load();
    GraphicsContext context2D = canvas.getGraphicsContext2D();

    root.getChildren().add(canvas);
    Scene scene = new Scene(root, 600, 150);
    primaryStage.setScene(scene);
    primaryStage.setTitle("chrome://dino");
    primaryStage.setResizable(false);
    primaryStage.show();

    Game game = new Game(context2D, scene.getWidth(), scene.getHeight());
    new AnimationTimer() {
      @Override
      public void handle(long now) {
        Time.update(now);
        context2D.clearRect(0, 0, canvas.getWidth(), canvas.getHeight());
        game.update();
      }
    }.start();
    scene.setOnKeyPressed(event -> game.onKeyPressed(event.getCode()));
    scene.setOnKeyReleased(event -> game.onKeyReleased(event.getCode()));
  }
}
